defmodule Paren do
  def count(?() do
    1
  end
  def count(?)) do
    -1
  end
  def find_basement(list) do
    do_find_basement(list, 0, 0)
  end



  def do_find_basement(_, -1, letter_number) do
    IO.puts letter_number
  end

  def do_find_basement([head | tail] , current, letter_number) do
    IO.puts "#{head}\t#{current}\t #{letter_number}"
    do_find_basement(tail, current + head, letter_number + 1)
  end

  def do_find_basement(_list, _current, _letter_number) do
    #IO.puts letter_number
    IO.puts "nothing :("
  end

end

